function intersection(x, y) { var m = x.length; var n = y.length; // c 是 (m + 1) * (n + 1) 的二维数组 var c = []; for (var i = 0; i <= m; i++) { c[i] = [0]; } for (var j = 0; j <= n; j++) { c[0][j] = 0; } // 利用动态规划，自底向上求解 for(var i = 1; i <= m; i++) { for(var j = 1; j <= n; j++) { if (x[i-1] == y[j-1]) { c[i][j] = c[i-1][j-1] + 1; } else if (c[i - 1][j] >= c[i][j - 1]) { c[i][j] = c[i - 1][j]; } else { c[i][j] = c[i][j - 1]; } } } print(c, x, y, m, n);
} function print(c, x, y, i, j) { if (i == 0 || j == 0) { return; } if (x[i-1] == y[j-1]) { print(c, x, y, i-1, j-1); // console.log(x[i-1]); intersectionArr.push(x[i - 1]); } else if (c[i - 1][j] >= c[i][j - 1]) { print(c, x, y, i - 1, j); } else { print(c, x, y, i, j - 1); }
}
// 交集结果
var intersectionArr = [];
intersection([1, 2, 2, 1], [2, 2]);
// intersection([1, 2, 2, 1, 4, 5], [2, 2, 1, 3, 5]);

